function calculateStepDuration(cfg) {
    const speed = Number(cfg?.speed) || 0;
    const stepValue = Math.abs(Number(cfg?.step) || 0);
    if (speed <= 0 || stepValue <= 0) {
        return 0;
    }
    const start = Number(cfg?.start) || 0;
    const end = Number(cfg?.end) || 0;
    const distance = Math.abs(end - start);
    const iterations = distance === 0 ? 1 : Math.ceil(distance / stepValue);
    return Math.max(0, Math.round(iterations * speed));
}

function updatePlanEstimates(steps) {
    let cyclePlan = 0;
