    const now = Date.now();
    let duration = 0;
    if (Number.isFinite(state.stepStartedAt) && state.stepStartedAt > 0) {
        duration = Math.max(0, now - state.stepStartedAt);
    }
    if (reason === 'skip') {
        duration = 0;
    }
    updateStepTime(state.currentStep, duration);
    state.stepStartedAt = 0;

    if (reason) {
        broadcast(reason, false);
    }

    // Если цикл заканчивается на последнем шаге (в том числе при пропуске), ждём подтверждение вывода графика
    if (isLastStep() && (reason === 'completed' || reason === 'skip')) {
        const cycleDuration = (Number.isFinite(state.cycleStartedAt) && state.cycleStartedAt > 0)
            ? Math.max(0, now - state.cycleStartedAt)
            : 0;
        global.set('ITERATION_CYCLE_TIME', Math.round(cycleDuration));
        state.cycleStartedAt = 0;
        state.flushPending = true;
        broadcast('cycleComplete');
        return;
    }

    // ИСПРАВЛЕНИЕ #3: Если шаг пропущен, не делаем паузу
    if (reason === 'skip') {
        advanceStep('nextStep');
        return;
    }

    const pause = Number(cfg.pause) || 0;
    if (pause > 0) {
        state.pauseTimer = setTimeout(() => {
            state.pauseTimer = null;
            // Повторная проверка состояния перед переходом
            if (state.control !== 1) {
                return;
            }
            broadcast('pauseHold');
            advanceStep('pauseComplete');
        }, pause);
    } else {
        // ИСПРАВЛЕНИЕ: Если паузы нет, сразу переходим к следующему шагу
        advanceStep('nextStep');
    }
}

// === ЛОГИКА ПЕРЕХОДА К СЛЕДУЮЩЕМУ ШАГУ ===

function advanceStep(reason) {
    clearAllTimers();

    if (state.flushPending) {
        return;
    }

    if (!Array.isArray(state.steps) || !state.steps.length) {
        return;
    }

    // Вычисление следующего шага
    let nextStep = state.currentStep ? state.currentStep + 1 : 1;
    if (nextStep > state.steps.length) {
        nextStep = 1;
    }

    setCurrentStep(nextStep);
    const cfg = getStepConfig(state.currentStep);
    if (!cfg) {
        return;
    }

    // ИСПРАВЛЕНИЕ #6: Проверка на пропуск ДО установки значения
    if (shouldSkipStep(cfg)) {
        state.skipChain = (typeof state.skipChain === 'number' ? state.skipChain : 0) + 1;
        updateStepTime(state.currentStep, 0);
        // При пропуске шага значение НЕ меняется
        broadcast('skip', false);

        if (state.skipChain >= state.steps.length) {
            setControl(0);
            broadcast('skipLimit', false);
            state.skipChain = 0;
            return;
        }

        finishStep('skip');
        return;
    }

    state.skipChain = 0;

    if (state.currentStep === 1) {
        markCycleStart();
    }

    // Установка начального значения только если шаг НЕ пропускается
    const startValue = Number(cfg.start);
    setValue(Number.isFinite(startValue) ? startValue : 0);
    state.stepStartedAt = Date.now();
    broadcast(reason || 'stepStart');

    // Проверка состояния перед запуском таймера
    if (state.control !== 1) {
        return;
    }

    scheduleNextTick();
}

// === ЛОГИКА ПЛАНИРОВАНИЯ СЛЕДУЮЩЕГО ТИКА ===

function scheduleNextTick(delayOverride) {
    // ИСПРАВЛЕНИЕ #4: Защита от race conditions
    if (state.processing) {
        return;
    }

    if (state.flushPending) {
        return;
    }

    clearMainTimer();

    // ИСПРАВЛЕНИЕ #5: Проверка состояния контроллера
    if (state.control !== 1) {
        return;
    }

    const cfg = getStepConfig(state.currentStep);
    if (!cfg) {
        return;
    }

    if (shouldSkipStep(cfg)) {
        finishStep('skip');
        return;
    }

    // ИСПРАВЛЕНИЕ #7: Убрана компенсация задержек, простой setTimeout
    const delay = delayOverride != null ? delayOverride : (Number(cfg.speed) || 0);
    if (delay <= 0) {
        return;
    }

    // ИСПРАВЛЕНИЕ #4: Установка флага обработки
    state.processing = true;

    state.timer = setTimeout(() => {
        state.timer = null;
        // ИСПРАВЛЕНИЕ #4: Сброс флага обработки
        state.processing = false;

        // ИСПРАВЛЕНИЕ #5: Проверка состояния после таймера
        if (state.control !== 1) {
            return;
        }

        const cfgInner = getStepConfig(state.currentStep);
        if (!cfgInner) {
            return;
        }

        const stepValue = Math.abs(Number(cfgInner.step) || 0);
        if (stepValue === 0) {
            finishStep('skip');
            return;
        }

        const startValue = Number(cfgInner.start);
        const endValue = Number(cfgInner.end);
        const direction = endValue >= startValue ? 1 : -1;
        const increment = stepValue * direction;

        let nextValue = state.value + increment;

        // Ограничение по конечному значению
        if (direction > 0) {
            if (nextValue > endValue) {
                nextValue = endValue;
            }
        } else {
            if (nextValue < endValue) {
                nextValue = endValue;
            }
        }

        setValue(nextValue);
        broadcast('tick');

        // Проверка достижения конечного значения
        const reachedEnd = (direction > 0 && nextValue >= endValue) || (direction < 0 && nextValue <= endValue);
        if (reachedEnd) {
            finishStep('completed');
        } else {
            scheduleNextTick();
        }
    }, delay);
}

// === ИНИЦИАЛИЗАЦИЯ ЗНАЧЕНИЯ ПРИ ПЕРВОМ ЗАПУСКЕ ===

if (!Number.isFinite(state.value)) {
    const cfg = getStepConfig(state.currentStep);
    const startValue = cfg ? Number(cfg.start) : 0;
    setValue(Number.isFinite(startValue) ? startValue : 0);
}

// === ОБРАБОТКА КОМАНД ===

const action = msg.action || msg.topic || '';

switch (action) {
    case 'restart':
        clearAllTimers();
        state.flushPending = false;

        if (Array.isArray(msg.steps) && msg.steps.length) {
            state.steps = msg.steps;
        }

        if (!Array.isArray(state.steps) || !state.steps.length) {
            break;
        }

        setCurrentStep(1);
        const firstCfg = getStepConfig(state.currentStep);

        if (firstCfg) {
            markCycleStart();
            if (shouldSkipStep(firstCfg)) {
                // Не устанавливаем значение при пропуске
                setControl(2);
                broadcast('restart');
                setControl(1);
                broadcast('run', false);
                finishStep('skip');
            } else {
                const startValue = Number(firstCfg.start);
                setValue(Number.isFinite(startValue) ? startValue : 0);
                state.stepStartedAt = Date.now();
                setControl(2);
                broadcast('restart');
                setControl(1);
                broadcast('run', false);
                scheduleNextTick();
            }
        }
        break;

    case 'start':
        if (!Array.isArray(state.steps) || !state.steps.length) {
            break;
        }

        state.flushPending = false;

        if (state.control === 1) {
            // Уже запущен, просто возобновляем если нужно
            if (!state.timer && !state.pauseTimer && !state.processing) {
                scheduleNextTick();
            }
            broadcast('resume', false);
            break;
        }

        setControl(1);
        if (!Number.isFinite(state.cycleStartedAt) || state.cycleStartedAt <= 0) {
            markCycleStart();
        }
        clearPauseTimer();
        broadcast('resume', false);
        scheduleNextTick();
        break;

    case 'stop':
        clearAllTimers();
        state.flushPending = false;
        setControl(0);
        broadcast('stop', false);
        break;

    case 'updateParameters':
        if (Array.isArray(msg.steps) && msg.steps.length) {
            state.steps = msg.steps;
        }

        const currentCfg = getStepConfig(state.currentStep);
        if (currentCfg) {
            const startVal = Number(currentCfg.start);
            const endVal = Number(currentCfg.end);

            if (Number.isFinite(startVal) && Number.isFinite(endVal)) {
                const min = Math.min(startVal, endVal);
                const max = Math.max(startVal, endVal);

                if (!Number.isFinite(state.value) || state.value < min || state.value > max) {
                    setValue(startVal);
                    broadcast('parameters');
                } else {
                    broadcast('parameters', false);
                }
            } else {
                broadcast('parameters', false);
            }
        } else {
            broadcast('parameters', false);
        }

        if (state.control === 1 && !state.processing) {
            scheduleNextTick();
        }
        break;

    case 'flushResume':
        if (state.flushPending) {
            state.flushPending = false;
            if (state.control === 1) {
                advanceStep('cycleResume');
            }
        }
        break;

    default:
        if (state.control === 1 && !state.timer && !state.pauseTimer && !state.processing) {
            scheduleNextTick();
        }
        break;
}

// Сохранение состояния
context.set('state', state);
return null;

const reason = msg.reason || '';
const command = msg.command || '';
const outputs = [null, null, null, null, null, null];
const MAX_BUFFER_SIZE = 20000;
const EXPORT_KEY = 'trendExportData';
const EXPORT_READY_KEY = 'trendExportReady';

function ensureBuffer() {
    const current = flow.get('trendBuffer');
    if (current && typeof current === 'object') {
        const labels = Array.isArray(current.labels) ? current.labels : [];
        const value = Array.isArray(current.value) ? current.value : [];
        const step = Array.isArray(current.step) ? current.step : [];
        return { labels, value, step };
    }
    return { labels: [], value: [], step: [] };
}

function formatTimestamp(ts) {
    const numeric = Number(ts);
    if (Number.isFinite(numeric)) {
        try {
            return new Date(numeric).toISOString();
        } catch (err) {
            // ignore
        }
    }
    return new Date().toISOString();
}

function getCycleStartTime() {
    const stored = flow.get('cycleStartTime');
    return Number.isFinite(Number(stored)) ? Number(stored) : null;
}

function setCycleStartTime(ts) {
    if (Number.isFinite(Number(ts))) {
        flow.set('cycleStartTime', Number(ts));
    }
}

function appendPoint(buffer, value, step, ts) {
    if (!Number.isFinite(value) || !Number.isFinite(step)) {
        return;
    }

    while (buffer.labels.length >= MAX_BUFFER_SIZE) {
        buffer.labels.shift();
        buffer.value.shift();
        buffer.step.shift();
    }

    // Временная шкала всегда начинается с нуля
    // Если буфер пуст, это первая точка - время = 0
    // Если буфер не пуст, вычисляем время относительно первой точки
    let relativeTime = 0;
    if (buffer.labels.length === 0) {
        // Первая точка всегда имеет время 0
        relativeTime = 0;
        // Сохраняем timestamp первой точки для последующих вычислений
        if (Number.isFinite(Number(ts))) {
            setCycleStartTime(Number(ts));
        }
    } else {
        // Вычисляем время относительно первой точки
        const cycleStart = getCycleStartTime();
        if (cycleStart !== null && Number.isFinite(Number(ts))) {
            relativeTime = Math.max(0, Number(ts) - cycleStart);
        } else {
            // Если cycleStart не установлен, используем последнее время + минимальный шаг
            const lastTime = buffer.labels.length > 0 ? Number(buffer.labels[buffer.labels.length - 1]) : 0;
            relativeTime = lastTime + 1; // Минимальный шаг 1 мс
        }
    }

    // Используем относительное время как timestamp (в миллисекундах от 0)
    buffer.labels.push(relativeTime);
    buffer.value.push(value);
    buffer.step.push(step);
}

function cloneBuffer(buffer) {
    return {
        labels: buffer.labels.slice(),
        value: buffer.value.slice(),
        step: buffer.step.slice()
    };
}

function toTimestamp(value) {
    if (value instanceof Date) {
        return value.getTime();
    }
    const parsed = Date.parse(value);
    if (Number.isFinite(parsed)) {
        return parsed;
    }
    const numeric = Number(value);
    if (Number.isFinite(numeric)) {
        return numeric;
    }
    return NaN;
}

function createSeriesPoints(labels, seriesValues) {
    const points = [];
    const length = Math.min(labels.length, seriesValues.length);
    for (let i = 0; i < length; i += 1) {
        // labels уже содержат относительное время в миллисекундах
        const x = Number(labels[i]);
        const y = Number(seriesValues[i]);
        if (Number.isFinite(x) && Number.isFinite(y)) {
            points.push({ x, y });
        }
    }
    return points;
}

function buildChartMessages(buffer, options = {}) {
    const { forceClear = false } = options;
    const messages = [];

    if (forceClear) {
        messages.push({ ui_control: { clear: true } });
    }

    const valueSeries = createSeriesPoints(buffer.labels, buffer.value);
    const stepSeries = createSeriesPoints(buffer.labels, buffer.step);

    if (valueSeries.length || stepSeries.length) {
        messages.push({
            payload: [
                {
                    series: ['ITERATION_VALUE', 'ITERATION_STEP'],
                    data: [valueSeries, stepSeries],
                    labels: []
                }
            ],
            topic: 'ITERATION_BUFFER'
        });
    }

    return messages;
}


function resetBuffer(buffer) {
    buffer.labels = [];
    buffer.value = [];
    buffer.step = [];
}

function storeExportSnapshot(snapshot) {
    flow.set(EXPORT_KEY, snapshot);
    flow.set(EXPORT_READY_KEY, snapshot.labels.length > 0);
}

function getExportSnapshot() {
    const data = flow.get(EXPORT_KEY);
    if (data && typeof data === 'object') {
        const labels = Array.isArray(data.labels) ? data.labels : [];
        const value = Array.isArray(data.value) ? data.value : [];
        const step = Array.isArray(data.step) ? data.step : [];
        return { labels, value, step };
    }
    return { labels: [], value: [], step: [] };
}

function clearExportSnapshot() {
    flow.set(EXPORT_KEY, { labels: [], value: [], step: [] });
    flow.set(EXPORT_READY_KEY, false);
}

const buffer = ensureBuffer();
const numericValue = Number(msg.payload);
const stepCandidate = Number(msg.step);
const stepValue = Number.isFinite(stepCandidate) ? stepCandidate : Number(global.get('ITERATION_STEP'));
const timestamp = Number.isFinite(Number(msg.ts)) ? Number(msg.ts) : Date.now();

if (reason === 'tick') {
    appendPoint(buffer, numericValue, stepValue, timestamp);
}

if (reason === 'restart' || reason === 'stop') {
    resetBuffer(buffer);
    clearExportSnapshot();
    if (reason === 'restart') {
        setCycleStartTime(timestamp);
    } else if (reason === 'stop') {
        flow.set('cycleStartTime', null);
    }
    outputs[0] = [{ ui_control: { clear: true } }];
    node.status({ fill: 'grey', shape: 'dot', text: 'Буфер очищен' });
}

const startTriggers = ['restart', 'nextStep', 'pauseComplete', 'pauseHold', 'cycleResume'];
if (startTriggers.includes(reason)) {
    if (reason === 'restart' || (reason === 'nextStep' && stepValue === 1)) {
        setCycleStartTime(timestamp);
    }
    appendPoint(buffer, numericValue, stepValue, timestamp);
}

if (reason && Number.isFinite(numericValue)) {
    outputs[2] = { payload: numericValue };
}

if (reason && Number.isFinite(stepValue)) {
    outputs[3] = { payload: stepValue };
}

if (command === 'manualFlush') {
    const snapshot = cloneBuffer(buffer);
    if (snapshot.labels.length) {
        outputs[0] = buildChartMessages(snapshot, { forceClear: true });
        node.status({ fill: 'blue', shape: 'ring', text: `Ручная отрисовка (${snapshot.labels.length})` });
    } else {
        node.status({ fill: 'yellow', shape: 'ring', text: 'Буфер пуст' });
    }
}

if (reason === 'cycleComplete') {
    const snapshot = cloneBuffer(buffer);
    const chartMessages = buildChartMessages(snapshot, { forceClear: true });
    outputs[0] = chartMessages.length ? chartMessages : [{ ui_control: { clear: true } }];
    storeExportSnapshot(snapshot);
    if (snapshot.labels.length) {
        node.status({ fill: 'blue', shape: 'dot', text: `Готово к экспорту (${snapshot.labels.length})` });
    } else {
        node.status({ fill: 'yellow', shape: 'ring', text: 'Нет данных для экспорта' });
    }
    outputs[4] = {
        action: 'flushResume',
        topic: 'flushResume',
        reason: 'cycleComplete',
        ts: Date.now()
    };
    resetBuffer(buffer);
}

if (command === 'exportTrend') {
    const ready = flow.get(EXPORT_READY_KEY);
    const snapshot = getExportSnapshot();
    if (ready && snapshot.labels.length) {
        outputs[5] = {
            topic: 'trendExport',
            payload: snapshot,
            ts: Date.now()
        };
        clearExportSnapshot();
        node.status({ fill: 'green', shape: 'dot', text: `Экспортировано ${snapshot.labels.length}` });
    } else {
        node.status({ fill: 'yellow', shape: 'ring', text: 'Нет данных для экспорта' });
    }
}

flow.set('trendBuffer', buffer);
return outputs;

// Вычисление теоретической траектории на основе параметров шагов
const topic = msg.topic || '';
if (topic !== 'updateTheoretical') {
    return null;
}

const steps = flow.get('steps') || flow.get('defaultSteps') || [];
if (!Array.isArray(steps) || steps.length === 0) {
    return [{ ui_control: { clear: true } }];
}

const valuePoints = [];
const stepPoints = [];
let currentTime = 0;

for (let i = 0; i < steps.length; i++) {
    const cfg = steps[i];
    const stepNumber = i + 1;
    const start = Number(cfg?.start) || 0;
    const end = Number(cfg?.end) || 0;
    const stepValue = Math.abs(Number(cfg?.step) || 0);
    const speed = Number(cfg?.speed) || 0;
    const pause = Number(cfg?.pause) || 0;

    // Пропуск шага если скорость = 0 или шаг = 0
    if (speed <= 0 || stepValue <= 0) {
        // Пропущенный шаг - значение не меняется, время не тратится
        continue;
    }

    // Начальная точка шага
    valuePoints.push({ x: currentTime, y: start });
    stepPoints.push({ x: currentTime, y: stepNumber });

    // Вычисление направления
    const direction = end >= start ? 1 : -1;
    const increment = stepValue * direction;

    // Вычисление всех промежуточных точек
    let currentValue = start;
    let tickTime = currentTime;

    while (true) {
        const nextValue = currentValue + increment;
        const reachedEnd = (direction > 0 && nextValue >= end) || (direction < 0 && nextValue <= end);

        tickTime += speed;
        const outputValue = reachedEnd ? end : nextValue;

        valuePoints.push({ x: tickTime, y: outputValue });
        stepPoints.push({ x: tickTime, y: stepNumber });

        currentValue = outputValue;

        if (reachedEnd) {
            currentTime = tickTime;
            break;
        }
    }

    // Добавление паузы после шага (кроме последнего)
    if (pause > 0 && i < steps.length - 1) {
        currentTime += pause;
        // Точка в конце паузы (значение не меняется)
        valuePoints.push({ x: currentTime, y: end });
        stepPoints.push({ x: currentTime, y: stepNumber });
    }
}

if (valuePoints.length === 0 || stepPoints.length === 0) {
    return [{ ui_control: { clear: true } }];
}

return [
    { ui_control: { clear: true } },
    {
        payload: [
            {
                series: ['ITERATION_VALUE_THEORETICAL', 'ITERATION_STEP_THEORETICAL'],
                data: [valuePoints, stepPoints],
                labels: []
            }
        ],
        topic: 'THEORETICAL_CHART'
    }
];

if (msg.reason === "tick") {
    return null;
}
const dataList = [];
const versionDisplay = flow.get('versionDisplay') || "";
const versionCode = flow.get('version') || "";
const descriptions = {
    'VER': 'Версия проекта (число)',
    'flow.version': 'Версия проекта (строка)',
    'versionDisplay': 'Отображаемая версия',
    'ITERATION_CONTROL': 'Управление (0-стоп, 1-старт, 2-рестарт)',
    'ITERATION_STEP': 'Номер текущего шага',
    'ITERATION_VALUE': 'Текущее значение итерации',
    'ITERATION_CYCLE_TIME': 'Время полного цикла (мс)',
    'ITERATION_CYCLE_TIME_PLAN': 'Расчётное время полного цикла (мс)'
};
dataList.push({ name: "VER", value: global.get('VER'), description: descriptions['VER'] });
dataList.push({ name: "flow.version", value: versionCode, description: descriptions['flow.version'] });
dataList.push({ name: "versionDisplay", value: versionDisplay, description: descriptions['versionDisplay'] });
dataList.push({ name: "ITERATION_CONTROL", value: global.get('ITERATION_CONTROL'), description: descriptions['ITERATION_CONTROL'] });
dataList.push({ name: "ITERATION_STEP", value: global.get('ITERATION_STEP'), description: descriptions['ITERATION_STEP'] });
dataList.push({ name: "ITERATION_VALUE", value: global.get('ITERATION_VALUE'), description: descriptions['ITERATION_VALUE'] });
const cycleRaw = global.get('ITERATION_CYCLE_TIME');
const cycleNumeric = Number(cycleRaw);
dataList.push({ name: "ITERATION_CYCLE_TIME", value: Number.isFinite(cycleNumeric) ? Math.max(0, Math.round(cycleNumeric)) : 0, description: descriptions['ITERATION_CYCLE_TIME'] });
const cyclePlanRaw = global.get('ITERATION_CYCLE_TIME_PLAN');
const cyclePlanNumeric = Number(cyclePlanRaw);
dataList.push({ name: "ITERATION_CYCLE_TIME_PLAN", value: Number.isFinite(cyclePlanNumeric) ? Math.max(0, Math.round(cyclePlanNumeric)) : 0, description: descriptions['ITERATION_CYCLE_TIME_PLAN'] });
const stepMessages = [];
for (let i = 1; i <= 5; i++) {
    const start = global.get(`ITER_START_${i}`);
    const speed = global.get(`ITER_SPEED_${i}`);
    const step = global.get(`ITER_STEP_${i}`);
    const end = global.get(`ITER_END_${i}`);
    const pause = global.get(`ITER_PAUSE_${i}`);
    const stepTimeRaw = global.get(`ITER_STEP_TIME_${i}`);
    const planTimeRaw = global.get(`ITER_STEP_TIME_PLAN_${i}`);
    const stepTimeNumeric = Number(stepTimeRaw);
    const planTimeNumeric = Number(planTimeRaw);
    const actualValue = Number.isFinite(stepTimeNumeric) ? Math.max(0, Math.round(stepTimeNumeric)) : 0;
    const planValue = Number.isFinite(planTimeNumeric) ? Math.max(0, Math.round(planTimeNumeric)) : 0;
    dataList.push({ name: `ITER_START_${i}`, value: start, description: `Начальное значение шага ${i}` });
    dataList.push({ name: `ITER_SPEED_${i}`, value: speed, description: `Скорость шага ${i} (мс)` });
    dataList.push({ name: `ITER_STEP_${i}`, value: step, description: `Шаг изменения шага ${i}` });
    dataList.push({ name: `ITER_END_${i}`, value: end, description: `Конечное значение шага ${i}` });
    dataList.push({ name: `ITER_PAUSE_${i}`, value: pause, description: `Пауза после шага ${i} (мс)` });
    dataList.push({ name: `ITER_STEP_TIME_${i}`, value: actualValue, description: `Длительность шага ${i} (мс)` });
    dataList.push({ name: `ITER_STEP_TIME_PLAN_${i}`, value: planValue, description: `Расчётная длительность шага ${i} (мс)` });
    stepMessages.push({
        step: i,
        payload: actualValue,
        plan: planValue,
        topic: `stepTime${i}`
    });
}
msg.payload = dataList;
return [msg, stepMessages];

const reason = msg.reason || '';
if (reason === 'tick') {
    return null;
}
let text = '';
switch (reason) {
    case 'restart':
        text = 'Контроллер перезапущен';
        break;
    case 'run':
    case 'resume':
        text = 'Контроллер запущен';
        break;
    case 'stop':
        text = 'Контроллер остановлен';
        break;
    case 'parameters':
        text = 'Параметры обновлены';
        break;
    case 'pauseComplete':
        text = 'Пауза завершена';
        break;
    case 'nextStep':
    case 'stepStart':
        text = `Начат шаг ${msg.step}`;
        break;
    case 'skip':
        text = `Шаг ${msg.step} пропущен (скорость=0 или шаг=0)`;
        break;
    default:
        text = `Состояние обновлено (${reason || 'без причины'})`;
}
text += ` (управление=${msg.control})`;
msg.payload = text;
return msg;

msg.action = 'restart';
const steps = flow.get('steps');
if (Array.isArray(steps)) {
    msg.steps = steps;
}
const theoreticalMsg = {
    topic: 'updateTheoretical'
};
return [msg, theoreticalMsg];

const filename = flow.get('configFile');
if (!filename) {
    node.warn('Файл конфигурации не задан');
    return [null, { payload: 'Файл конфигурации не задан' }];
}
const requestMsg = {
    filename,
    source: 'файл',
    topic: 'loadParameters'
};
const statusMsg = {
    payload: 'Загрузка параметров из файла...'
};
return [requestMsg, statusMsg];

const steps = flow.get('steps') || flow.get('defaultSteps') || [];
const filename = flow.get('configFile');
if (!filename) {
    node.warn('Файл конфигурации не задан');
    return [null, { payload: 'Файл конфигурации не задан' }];
}
const saveMsg = {
    filename,
    payload: { steps }
};
const statusMsg = {
    payload: 'Сохранение параметров в файл...'
};
return [saveMsg, statusMsg];

const response = {
    version: flow.get('version'),
    versionDisplay: flow.get('versionDisplay'),
    control: global.get('ITERATION_CONTROL'),
    step: global.get('ITERATION_STEP'),
    value: global.get('ITERATION_VALUE'),
    steps: []
};
for (let i = 1; i <= 5; i++) {
    response.steps.push({
        index: i,
        start: global.get(`ITER_START_${i}`),
        speed: global.get(`ITER_SPEED_${i}`),
        step: global.get(`ITER_STEP_${i}`),
        end: global.get(`ITER_END_${i}`),
        pause: global.get(`ITER_PAUSE_${i}`)
    });
}
msg.payload = response;
return msg;

const snapshot = msg.payload || {};
const labels = Array.isArray(snapshot.labels) ? snapshot.labels : [];
const values = Array.isArray(snapshot.value) ? snapshot.value : [];
const steps = Array.isArray(snapshot.step) ? snapshot.step : [];
const length = Math.min(labels.length, values.length, steps.length);

if (!length) {
    node.status({ fill: 'yellow', shape: 'ring', text: 'Пустой набор данных' });
    return null;
}

// labels уже содержат миллисекунды от начала цикла (0, 114, 216...)
const lines = ['elapsed_ms;value;step'];
for (let i = 0; i < length; i += 1) {
    const elapsed = Number(labels[i]) || 0;
    lines.push(`${elapsed};${values[i]};${steps[i]}`);
}

const dirCandidate = flow.get('trendExportDir') || '/data';
const safeDir = String(dirCandidate).trim() || '/data';
const stamp = new Date().toISOString().replace(/[:.]/g, '-');
const filename = `${safeDir}/trend_${stamp}.csv`;

msg.payload = lines.join('\n');
msg.filename = filename;
msg.topic = 'trendExportFile';
node.status({ fill: 'green', shape: 'dot', text: `Файл готов (${length})` });
return msg;

const form = msg.payload || {};
const rawText = (form.text ?? '').toString().trim();
if (!rawText) {
    node.warn('Пустая заметка не сохранена');
    return null;
}

const now = new Date();
const timestamp = now.getTime();
const displayTime = now.toLocaleString('ru-RU', { hour12: false });

const versionCode = flow.get('version');
const versionDisplay = flow.get('versionDisplay');
const versionSource = [versionCode, versionDisplay].find((value) =>
    typeof value === 'string' && value.trim()
);

const note = {
    id: `note_${timestamp}`,
    text: rawText,
    createdAt: timestamp,
    createdIso: now.toISOString(),
    displayTime
};

if (versionSource) {
    note.version = versionSource.trim();
}

msg.note = note;
msg.payload = note;
msg.topic = 'preparedNote';
return msg;

const note = msg.note || msg.payload;
if (!note || typeof note.text !== 'string') {
    return null;
}

const notesFile = flow.get('notesFile');
if (!notesFile) {
    node.warn('Файл заметок не настроен');
    return [null, null];
}

const current = flow.get('notes');
const notes = Array.isArray(current) ? [...current] : [];
notes.unshift(note);
flow.set('notes', notes);

const updateMsg = {
    payload: notes,
    note,
    noteCount: notes.length,
    statusText: `Заметка сохранена (всего ${notes.length})`,
    topic: 'notesUpdate'
};

const fileMsg = {
    payload: notes,
    filename: notesFile,
    topic: 'saveNotes',
    noteCount: notes.length,
    statusText: `Заметки сохранены (${notes.length})`
};

return [updateMsg, fileMsg];

const statusText = msg.statusText || `Заметки сохранены (${msg.noteCount ?? 0})`;
msg.payload = statusText;
return msg;

const notesFile = flow.get('notesFile');
if (!notesFile) {
    node.warn('Файл заметок не задан');
    return [null, { payload: 'Файл заметок не задан' }];
}

const statusText = msg.topic === 'manualReload' ? 'Обновление заметок...' : 'Загрузка заметок...';

const requestMsg = {
    filename: notesFile,
    topic: 'loadNotes',
    source: 'файл'
};

const statusMsg = {
    payload: statusText
};

return [requestMsg, statusMsg];

const raw = msg.payload;
const list = Array.isArray(raw) ? raw : [];
const versionDefault = [flow.get('version'), flow.get('versionDisplay')].find((value) =>
    typeof value === 'string' && value.trim()
);

const normalized = list
    .filter((item) => typeof item?.text === 'string' && item.text.trim() !== '')
    .map((item) => {
        const createdAt = Number(item.createdAt) || Date.parse(item.createdIso || '') || Date.now();
        const text = item.text.toString();
        const displayTime = typeof item.displayTime === 'string' && item.displayTime.trim()
            ? item.displayTime
            : new Date(createdAt).toLocaleString('ru-RU', { hour12: false });
        const id = typeof item.id === 'string' && item.id.trim() ? item.id : `note_${createdAt}`;
        const versionRaw = [item.version, item.versionCode, item.versionDisplay]
            .find((value) => typeof value === 'string' && value.trim());
        const version = (versionRaw || versionDefault);

        const note = {
            id,
            text,
            createdAt,
            createdIso: item.createdIso || new Date(createdAt).toISOString(),
            displayTime
        };

        if (version) {
            note.version = version.trim();
        }

        return note;
    })
    .sort((a, b) => b.createdAt - a.createdAt);

flow.set('notes', normalized);

const statusText = msg.statusText
    || (msg.topic === 'loadNotes'
        ? `Заметки загружены (${normalized.length})`
        : `Заметки обновлены (${normalized.length})`);

const uiMsg = {
    payload: normalized,
    topic: 'notesDisplay'
};

const statusMsg = statusText ? { payload: statusText, noteCount: normalized.length } : null;

msg.payload = normalized;
msg.noteCount = normalized.length;

return [uiMsg, statusMsg];
